mapper.select:
	SELECT mem_id, mem_pw, mem_nm, mem_img
    FROM member
    WHERE mem_id = #{memId}
    AND mem_pw = #{memPw}

mapper.update:
UPDATE member
SET mem_pw = #{memPw},
    mem_nm = #{memNm},
    mem_img = #{memImg}
WHERE mem_id = #{memId}

UPDATE member
SET cop_id = #{copId}, cop_yn = 'Y'
WHERE mem_id= #{memId}

mapper.select:
SELECT cop_id
FROM couple
WHERE mem_id = #{memId}
